var app=angular.module("useful.filters",[]);app.filter("utFilter",function(){return function(a,c){if(c!=""&&!_.isUndefined(c)&&!_.isNull(c)){var b;if(c.tag){b=new RegExp("\\b"+c.text+"\\b","i");return _.filter(a,function(d){return _.some(d.tags,function(e){return e.search(b)>-1})})}else{b=new RegExp(c.text,"i");return _.filter(a,function(d){return d.name.search(b)>-1||d.description.search(b)>-1||_.some(d.tags,function(e){return e.search(b)>-1})})}}return a}});